import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
export default function Timer2(props) {
    const socket=props.socket;
    const [time,setTime] =useState(0); 
    const [second,setsecond] =useState(0); 
    const [pre,setpre] = useState(180);
    var sec=9;
    var min=2;
    useEffect(()=>{
        socket.on("tx",(data)=>{
         setsecond(data.s);
         setTime(data.m);
        });
        console.log(sec,min,"lnd")
    },[socket])
    useEffect(()=>{
        if(second<10){setsecond(second.toString().padStart(2, '0'))};
        if(second===0){setsecond(59);setTime(x => x-=1);}

       },[second])
      const percentage = Math.round((pre*100)/180);
  return (

    <div>
      <CircularProgressbar 
      text={time.toString().padStart(2, '0') + ':' + second.toString().padStart(2, '0')}
      styles={buildStyles({
        textColor:'#fff',
        pathColor:'#f54e4e',
        tailColor:'rgba(255,255,255,.2)',
      })}
      />
    </div>
  )
}
