import {useContext, useState, useEffect, useRef} from "react";
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import "./board.css"
const red = '#f54e4e';
export default function Timer(props) {
  const [time,setTime] =useState(2); 
  const [second,setsecond] =useState(60); 
  const [pre,setpre] = useState(180);
  let x1 ;
  useEffect(()=>{
     x1=setInterval(()=>{
      if(props.isstart==='true'){
      setsecond(prev => prev -=1);
        setpre(prev => prev -=1);
      }
    },1000) 
   },[props.isstart])
   useEffect(()=>{
    console.log(props.isstart,"sex")
    console.log(x1);
    if(props.isstart==='false'){console.log('lnd');
      clearInterval(second++)
    } 
   },[props.isstart])

   useEffect(()=>{
    if(second===0){setsecond(59);setTime(x => x-=1);}
   },[second])
  const percentage = Math.round((pre*100)/180);

  return (
    <div>
      <CircularProgressbar 
      value={percentage}
      text={time + ':' + second}
      styles={buildStyles({
        textColor:'#fff',
        pathColor:'#f54e4e',
        tailColor:'rgba(255,255,255,.2)',
      })}
      />
    </div>
  )
}
